import React, { useState, useEffect } from "react";
import { useQuery } from "react-query";
import { useParams } from "react-router-dom";
import PageTemplate from "../components/templateActorListPage";
import { getActors } from "../api/tmdb-api";
import Spinner from "../components/spinner";
import ActorDetails from "../components/actorDetails";
import Pagination from "@mui/material/Pagination";

const ActorPage = () => {
  const { id } = useParams();
  const [page, setPage] = useState(1);

  const { data: actorData, error, isLoading, isError } = useQuery(
    ["actor", { page }],
    () => getActors(page),
    {
      keepPreviousData: true,
    }
  );

  useEffect(() => {
  }, [page]);

  if (isLoading) {
    return <Spinner />;
  }

  if (isError) {
    return <h1>{error.message}</h1>;
  }

  const actors = actorData?.results || [];
  const totalPages = actorData?.total_pages || 1;

  const handlePageChange = (event, value) => {
    setPage(value);
  };

  return (
    <>
      {actors.length > 0 ? (
        <>
          <PageTemplate title="Discover Actors" actor={actors}>
            <ActorDetails actor={actors} />
          </PageTemplate>
          <Pagination
            count={totalPages}
            page={page}
            onChange={handlePageChange}
            color="primary"
            shape="rounded"
            sx={{
              display: "flex",
              justifyContent: "center",
              margin: "20px 0",
            }}
          />
        </>
      ) : (
        <p>No actors found for this page.</p>
      )}
    </>
  );
};

export default ActorPage;
