import React, { useState } from "react";
import Header from "../headerActorList";
import FilterCard from "../filterActorsCard";
import ActorList from "../actorList";
import Grid from "@mui/material/Grid2";

function ActorListPageTemplate({ actors = [], title, selectFavorite }) {
  const [nameFilter, setNameFilter] = useState("");

  const displayedActors = actors.filter((actor) => 
     actor.name?.toLowerCase().includes(nameFilter.toLowerCase())
);

const handleChange = (type, value) => {
  if (type === "name") setNameFilter(value);
};

return (
  <Grid container>
    <Grid item xs={12}>
      <Header title={title} />
    </Grid>
    <Grid container spacing={3} sx={{ flex: "1 1 500px" }}>
      <Grid item xs={12} sm={6} md={4} lg={3} xl={2} sx={{ padding: "20px" }}>
        <FilterCard onUserInput={handleChange} titleFilter={nameFilter} />
      </Grid>
      <Grid item xs={12} sm={6} md={8} lg={9} xl={10}>
        <ActorList selectFavorite={selectFavorite} actors={displayedActors} />
      </Grid>
    </Grid>
  </Grid>
);
}
export default ActorListPageTemplate;
