import React from "react";
import ActorCard from "../actorCard"; // Assuming ActorCard is the component that displays each actor's info.
import Grid from "@mui/material/Grid2"; // MUI Grid component for layout.

const ActorList = ({ actors }) => {
  return (
    <Grid container spacing={2} sx={{ padding: "20px" }}>
      {actors.length > 0 ? (
        actors.map((actor) => (
          <Grid key={actor.id} item xs={12} sm={6} md={4} lg={3} xl={2}>
            <ActorCard actor={actor} /> {/* Render each actor card */}
          </Grid>
        ))
      ) : (
        <p>No actors found.</p> // Show a message if no actors are found.
      )}
    </Grid>
  );
};

export default ActorList;
