import React from "react";
import ActorCard from "../actorListCard";
import Grid from "@mui/material/Grid2";

const ActorList = (props) => {
  let actorCards = props.actors.map((actor) => (
    <Grid
      key={actor.id}
      item
      xs={12} // Full width on extra-small screens.
      sm={6} // Half width on small screens.
      md={4} // One-third width on medium screens.
      lg={3} // One-fourth width on large screens.
      xl={2} // One-sixth width on extra-large screens.
    >
      <ActorCard key={actor.id} actor={actor} action={props.action} />
    </Grid>
  ));

  return (
    <Grid container spacing={2} sx={{ padding: "20px" }}>
      {actorCards.length > 0 ? actorCards : <p>No actors found.</p>}
    </Grid>
  );
};

export default ActorList;
